mapper.update:
UPDATE wn_caritem
SET code = #{orderCode}
WHERE userid = #{userId} AND code is null

UPDATE wn_caritem
SET num = #{quantity}
WHERE userid = #{userId} AND goodid = #{goodId} AND code = #{orderCode}

UPDATE orders
SET totalPrice = #{totalPrice}
WHERE order_code = #{orderCode}

UPDATE orders
SET address_id = #{addressId}, receiver = #{receiver}, phone = #{phone}, address = #{address}
WHERE order_code = #{orderCode}

UPDATE orders
SET payed = 1
WHERE order_code = #{orderCode}
